sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if ((isLoading && !g_form.isNewRecord()) || newValue === '' || newValue === oldValue) {
		return;
	}
	//Type appropriate comment here, and begin script below
	var ga = new GlideAjax('checkEmailSI');
	ga.addParam('sysparm_name', 'getEmail');
	ga.addParam('sysparm_userID', newValue);
	ga.getXMLAnswer(callbackExtractEmail);

	function callbackExtractEmail(response){
		//var answer = response.responseXML.documentElement.getAttribute("answer");
		//alert(answer);
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email', response);
	}
}
